import type { APIRoute } from 'astro';
import type { Quote } from '../../../../components/atlas/types';



export const GET: APIRoute = ({ params, request }) => {

    const id = params.id!.replaceAll("_", " ").toLowerCase()



    let aux: any = {}
    database.filter((q: Quote) => q.codes.includes(id)).forEach((q: Quote) => {
        const respondentID = q.respondentID
        if (!(q.domain in aux)) {
            aux[q.domain] = {}
        }
        const domainData = aux[q.domain]
        if (!(respondentID in domainData)) {
            domainData[respondentID] = {
                respondentID,
                age: q.age,
                gender: q.gender,
                household: q.household,
                city: q.city,
                country: q.country,
                quotes: []
            }
        }
        const respondentData = domainData[respondentID]
        respondentData.quotes.push(q.text)
    })

    let data: any[] = []
    for (let domain in aux) {
        let quotes: any[] = [];
        for (let respondent in aux[domain]) {
            quotes.push(aux[domain][respondent])
        }
        data.push({
            name: domain,
            data: quotes,
        })
    }

    return new Response(
        JSON.stringify({
            success: true,
            data,
            message: ""
        })
    )
}

import { allCodes } from '../../schema/describe_code/[id]';
export function getStaticPaths() {
    return Object.keys(allCodes).map((id: string) => {
        return {
            params: { id }
        }
    })
}

